import json
import certifi
import requests
ROBLOSECURITY = 'CAEaAhAB.'
ROBLOX_ID = '1119237244'
headers = {'User-Agent': 'Mozilla/5.0', 'Referer': 'https://www.roblox.com/', 'Cookie': f'.ROBLOSECURITY={ROBLOSECURITY}'}
url = 'https://accountsettings.roblox.com/v1/email'
r = requests.get(url, headers=headers, verify=certifi.where(), timeout=15)
print('STATUS', r.status_code)
try:
    js = r.json()
    print(json.dumps(js, ensure_ascii=False, indent=2))
except Exception:
    print('Response text:', r.text)